IdentityAPI.developerLicenseByTokenId:
query GetDevLicenseByTokenId {
  developerLicense (by: { tokenId: <dev_license_token_id> } ) {
    owner
    tokenId
    alias
    clientId
    mintedAt
    redirectURIs (first: 10) {
      nodes {
        uri
        enabledAt
      }
    }
  }
}

IdentityAPI.sharedVehicles:
query GetVehicleByDevLicense {
  vehicles(filterBy: { privileged: "<dev_license_0x>" }, first: 100) {
    nodes {
      owner
      tokenId
      definition {
        make
        model
        year
      }
    }
  }
}

IdentityAPI.vehiclesByWalletAddress:
query UserVehiclesByWallet {
          vehicles(filterBy: { privileged: ${address}}, first: 100) {
            nodes {
              tokenId
              definition {
                make
                model
                year
              }
            },
            totalCount
          }
        }

IdentityAPI.mmyByOwner:
query GetVehicleMMYByOwner {
  vehicles(filterBy: {owner: "<0x_Address>"}, first: 100) {
    nodes {
      tokenId
      definition {
        make
        model
        year
      }
    }
  }
}

IdentityAPI.mmyByTokenId:
query GetVehicleMMYByTokenId {
  vehicle (tokenId: <token_id>) {
    owner
    definition {
      make
      model
      year
    }
  }
}

IdentityAPI.vehicleSacd:
query GetSacdForVehicle {
  vehicle (tokenId: 3) {
    sacds (first: 10) {
      nodes {
        permissions
        grantee
        source
        createdAt
        expiresAt
      }
    }
  }
}

IdentityAPI.rewardsHistoryByOwner:
query GetRewardHistoryByOwner {
  vehicles(filterBy: {owner: "<0x_address>"}, first: 10) {
    nodes {
      earnings {
        history (first: 10) {
          edges {
            node {
              week
              aftermarketDeviceTokens
              syntheticDeviceTokens
              sentAt
              beneficiary
              connectionStreak
              streakTokens
            }
          }
        }
        totalTokens
      }
    }
  }
}

IdentityAPI.definitionByDeviceId:
query GetDefinitionByDeviceId {
  deviceDefinition (by: { id: "<id_from_tableland>"})  {
    year
    model
    attributes {
      name
      value
    }
  }
}
